<style>
	.navbar-wrapper {
		background: var(--bg-color-1);
		height: 3rem;
		width: 100%;
		display: flex;
	}

	.navbar-wrapper.loading {
		box-shadow: 0 0px 20px 0px var(--color-primary);
		transition: box-shadow 0.5s ease;
	}

	.navbar {
		width: 100%;
		display: flex;
		flex-direction: row;
		justify-content: flex-start;
		align-items: center;
	}

	.nav-brand {
		height: 100%;
		padding: 0.5rem 0;
	}

	.brand-image {
		height: 1.2rem;
		fill: var(--color-primary);
	}

	.nav-links {
		margin-left: 4rem;
		display: flex;
		gap: 1rem;
		flex-direction: row;
		justify-content: flex-start;
	}

	.nav-link {
		color: var(--text-color-primary);
		font-family: var(--primary-font-family);
		font-weight: 300;
		font-size: 1rem;
		text-transform: lowercase;
	}

	@media screen and (max-width: 1023px) {
		.navbar-wrapper {
			height: 7vh;
		}

		.navbar {
			justify-content: space-between;
		}

		.brand-image {
			height: 0.875rem;
		}

		.nav-links {
			display: flex;
			flex-direction: row;
			gap: 1rem;
			margin: 0;
		}

		.nav-links .nav-link {
			text-align: center;
			font-size: 0.75rem;
		}
	}
</style>
